package toDoList;

import java.util.Arrays;
import java.util.Scanner;

public class App {

    public static void main(String[] args) {
        ToDoList toDoListy = new ToDoList();
        String choice = "0";

        try {
            Scanner userInput = new Scanner(System.in);

            while (!choice.equals("4")) {
                Menus.mainMenu(toDoListy.completedTasksCount(), toDoListy.notCompletedTasksCount());
                choice = userInput.nextLine();

                switch (choice) {
                    case "1":
                        toDoListy.showFormattedList();
                        Menus.showListMenu();
                        toDoListy.showTasksByDateOrProject(userInput.nextLine());
                        break;
                    case "2":
                        toDoListy.addTask();
                        break;
                    case "3":
                        toDoListy.showFormattedList();
                        toDoListy.removeTask();
                        break;
                    case "4":
                        break;
                    default:
                        System.out.println("\nWe don't have that choice, please type a number from given choices!");
                        Thread.sleep(3000);
                }
            }

            System.out.println("Bye, Bye");
        } catch (Exception e) {
            System.out.println(e.getMessage());
            System.out.println(Arrays.toString(e.getStackTrace()));
        }

    }
}
